def addStrings(n1,n2):
    ln1=len(n1)
    ln2=len(n2)
    count=0
    min=0
    if(ln1>ln2):
        max=ln1
        min=ln2
        count=ln1-ln2-1
    else:
        max=ln2
        min=ln1
        count=ln2-ln1-1
    carry=0
    res=''
    l=''
    #max=0
    diff=min
    sum=''
    while (ln1>=1 or ln2 >=1):
        diff-=1
        print("COUNT:",count,"DIFF:",diff)
        if(n1[ln1-1] is None):
            x=0
        else:
            x=n1[ln1-1]
        if(n2[ln2-1] is None):
            y=0
        else:
            y=n2[ln2-1]
        if (count!=diff) :
            sum=ord(x)+ord(y)-2*ord('0')+carry
            carry=sum//10
            l=sum%10
            res=str(l)+res
            ln1=ln1-1
            ln2=ln2-1
            max=max-1
        if(max==0 and ln1==0 and ln2==0):
            print("IN IF:",str(sum))
            res=str(sum)+res
            print("IN IF:",res)
        elif (max==0 and ln1==0 and ln2==-1):
            print("IN 2bd IF:",res,x)
            res=x+res
        elif (max==0 and ln1==-1 and ln2==0):
            print("IN 3rd IF:",str(sum))
            res=y+res
        print(ln1,ln2,"RES:",res,"SUM:",sum,"CARRY:",carry,"L:",l,"MAX:",max)
    return res

